, $this->request->get('channel')];        //时间搜索  时间戳搜素        $where['create_time'] = $this->parseRequestDate();        //状态        $where['status'] = ['eq', $this->request->get('status',OrderStatusEnum::UNPAID)];        //halt($this->logicOrders->getOrderList($where,true, 'create_time desc', 10));        $this->assign('list', $this->logicOrders->getOrderList($where,true, 'create_time desc', 10));        return $this->fetch();    }    /**     *     * @author 勇敢的小笨羊 <brianwaring98@gmail.com>     *     * @return mixed     */    public function submit(){        $where = ['uid' => is_login()];        $this->assign('list', $this->logicOrders->getOrderList($where,true, 'create_time desc', 10));        return $this->fetch();    }}